/**
 * Listings Page
 * 
 * Displays a grid of available properties:
 * - Fetches properties from the API on mount
 * - Shows loading state while fetching
 * - Renders properties in a responsive grid
 * - Each property is displayed as a card with key details
 */

import React, { useState, useEffect } from 'react';
import ListingCard from '../components/ListingCard';

interface Property {
  id: number;
  title: string;
  description: string;
  price: number;
  images: string[];
  owner: {
    name: string;
  };
}

const ListingsPage: React.FC = () => {
  const [properties, setProperties] = useState<Property[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        const response = await fetch('http://localhost:4000/api/properties');
        if (!response.ok) {
          throw new Error('Failed to fetch properties');
        }
        const data = await response.json();
        setProperties(data);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'An error occurred');
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-red-500 text-center">
          <h2 className="text-xl font-semibold mb-2">Error</h2>
          <p>{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-8">Available Properties</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {properties.map((property) => (
          <ListingCard
            key={property.id}
            id={property.id}
            title={property.title}
            description={property.description}
            price={property.price}
            images={property.images}
            owner_name={property.owner.name}
          />
        ))}
      </div>
    </div>
  );
};

export default ListingsPage;
