/**
 * Listings Page
 * 
 * Displays a grid of available properties:
 * - Fetches properties from the API on mount
 * - Shows loading state while fetching
 * - Renders properties in a responsive grid
 * - Each property is displayed as a card with key details
 */

import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { SearchBar } from '../components/SearchBar';

interface Property {
  id: number;
  title: string;
  description: string;
  price: number;
  images: string[];
  owner_name: string;
}

interface FilterParams {
  search?: string;
  minPrice?: number;
  maxPrice?: number;
  available?: string;
}

export const ListingsPage: React.FC = () => {
  const [properties, setProperties] = useState<Property[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [filters, setFilters] = useState<FilterParams>({});

  const fetchProperties = async (params: FilterParams = {}) => {
    try {
      setLoading(true);
      const queryParams = new URLSearchParams();

      if (params.search) queryParams.append('search', params.search);
      if (params.minPrice) queryParams.append('minPrice', params.minPrice.toString());
      if (params.maxPrice) queryParams.append('maxPrice', params.maxPrice.toString());
      if (params.available) queryParams.append('available', params.available);

      const response = await fetch(`/api/properties?${queryParams.toString()}`);
      if (!response.ok) throw new Error('Failed to fetch properties');

      const data = await response.json();
      setProperties(data);
      setError(null);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProperties(filters);
  }, [filters]);

  const handleFilter = (params: FilterParams) => {
    setFilters(params);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="text-red-500">Error: {error}</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Available Properties</h1>

      <SearchBar onSearch={handleFilter} />

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {properties.map((property) => (
          <Link
            key={property.id}
            to={`/listings/${property.id}`}
            className="block bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200"
          >
            <div className="aspect-w-16 aspect-h-9">
              <img
                src={property.images[0] || '/placeholder.jpg'}
                alt={property.title}
                className="object-cover rounded-t-lg w-full h-48"
              />
            </div>
            <div className="p-4">
              <h2 className="text-xl font-semibold mb-2">{property.title}</h2>
              <p className="text-gray-600 mb-2 line-clamp-2">{property.description}</p>
              <div className="flex justify-between items-center">
                <span className="text-lg font-bold">${property.price}/night</span>
                <span className="text-sm text-gray-500">by {property.owner_name}</span>
              </div>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};
