/**
 * User Dashboard Page
 * 
 * Displays a list of bookings made by the user:
 * - Shows pending bookings with cancel action
 * - Shows confirmed bookings with cancel action
 * - Handles loading states and error messages
 */

import React, { useState } from 'react';
import BookingTable from '../components/BookingTable';

const UserDashboardPage: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleCancel = async (bookingId: number) => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(`http://localhost:4000/api/bookings/${bookingId}/cancel`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.error || 'Failed to cancel booking');
      }

      alert('Booking cancelled successfully');
      // Refresh the booking list
      window.location.reload();
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to cancel booking');
      alert(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-8">My Dashboard</h1>
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <BookingTable 
          isOwner={false} 
          onCancel={handleCancel}
          loading={loading}
        />
      </div>
    </div>
  );
};

export default UserDashboardPage;
